mapper.insert:
insert into jp_community 
values(jp_community_seq.nextval, #{article_title}, #{article_content}, #{article_file1}, #{article_file2}, sysdate)
where mem_id = #{mem_id}

mapper.update:
update jp_community
set article_content = #{article_content}, article_title = #{article_title}, 
article_file1 = #{article_file1}, article_file2 = #{article_file2}
where mem_id = #{mem_id}
